import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrain } from "@fortawesome/free-solid-svg-icons";
import "../../../Css/Payment.css"

class TrainStatus extends Component {

    constructor(){
        super()
        this.state={

            trainNo : "",
            trainName : "",
            src : "",
            dest : ""

        }

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    componentDidMount() {
        this.state = {
          show: false
        };
      }

    showTable() {
        this.setState({
          show: true
        });
      }





    handleChange(event){
        this.setState({
            [ event.target.name ] : event.target.value
          })
      }

      async handleSubmit(event){
        event.preventDefault();

        this.props.history.push('/Dashboard/Receipt');  
     }




    render() {
        return (

            <div  id="trainstatus-main" className="trainstatus">

                <div className="trainstat">

                    <h3>Train Status</h3>

                    <div className = "status-textbox">

                    <FontAwesomeIcon icon = {faTrain} />
                    <input className= "TrNo" name="trainNo" type="text" value={this.state.trainNo} onChange={this.handleChange} placeholder="Enter Train Number" required>

                    </input>



                    </div>

                    <div className = "status-button">
                    <button onclick="showTable()">Check Status</button>

                    </div>

                    <div className = "status-table">

                    {
                        this.state.show &&
                        <table className="table">
                            <thead>
                                 <tr>
                                    <th>EmployeeID</th>
                                    <th>FirstName</th>
                                    <th>LastName</th>
                                    <th>Gender</th>               
                                </tr>
                            </thead>
                             <tbody>
                                {rows}
                            </tbody>
                         </table>
}

                    </div>

                </div>

            </div>



        )
    }
}

export default TrainStatus
