import React, { Component } from 'react'
import '../../../Css/Passenger.css'

class Passenger extends Component {
    constructor(props){
        super(props)
        this.state={

        pax : [{
                name:"",
                age: 0,
                sex : "",
                berth : ""
            }]   

        }
        this.handleSubmit=this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(event){
        this.setState({
            [ event.target.name ] : event.target.value
          })
    }

    addPax = e => {
        e.preventDefault()
        let pax = this.state.pax.concat([{
            name:"",
            age: 0,
            sex : "",
            berth : ""
        }])

        this.setState({
            pax
        })
    }



    async handleSubmit(event){
        event.preventDefault();

        console.log(this.state.pax)

        let path = `/Payment`;
        this.props.history.push(path)

     }



    render() {
        return (
            <div id="main-content" >
                <div id="train-deets">
                <h4> Passenger Details: </h4>

                    <table className="table table-bordered table-xs-responsive table-hover">
                        <thead className="thead-dark">  
                        <tr>
                            <th>Name</th>
                            <th>Age</th>
                            <th>Sex</th>
                            <th>Berth Preference</th>


                        </tr>
                        </thead>
                        <tbody>
                        {
                             this.state.pax.map((item) => (
                                <tr>

                                    <td><input classname = "Name" name="name" type="text" value={this.state.names} onChange={this.handleChange} placeholder="Name" required/></td>

                                    <td><input classname ="Age" name="age" placeholder="Age" type="number" min="0" max="100" value={this.state.age} onChange={this.handleChange}  required/></td>

                                    <td>
                                    <select classnName="Sex" name="Sex" onChange={this.handleChange} value={this.state.sex}>
                                    <option value="M">Male</option>
                                    <option value="F">Female</option>
                                    <option value="T">Transgender</option>
                                    </select>
                                    </td>

                                    <td>
                                    <select classnName="Berth" name="Berth" onChange={this.handleChange} value={this.state.berth}>
                                    <option value="LB">Lower Berth</option>
                                    <option value="MB">Middle Berth</option>
                                    <option value="UB">Upper Berth</option>
                                    <option value="SL">Side Lower</option>
                                    <option value="SU">Side Upper</option>
                                    </select>
                                    </td>

                                    <td><button onClick={this.addPax} className="btn btn-primary pull-right">+</button></td>


                                </tr>
                            ))
                        }

                        </tbody>
                    </table>
                </div>

                <input className="pax-submit" type="submit" onClick={this.handleSubmit} value="Pay"/><br/>


                </div>

        )
    }
}



export default Passenger
